const postcss = require('postcss');
const expect = require('chai').expect;
const fs = require('fs');
const plugin = require('../');

describe('harmonize', () =>
{
	it('inject styles', done =>
	{
		const input = fs.readFileSync(__dirname + '/provider/input.css', 'utf8');

		postcss(
		[
			plugin
		])
		.process(input,
		{
			from: null
		})
		.then(result =>
		{
			expect(result.css).not.to.equal(input);
			done();
		});
	});
});
